# PART I

IO.puts Enum.reduce(santa, 0, fn
(?(, acc) -> acc + 1
(?), acc) -> acc - 1
end)

# PART II

{ -1, idx } = Enum.reduce_while(santa, {0, 0}, fn
(?(, {floor, idx}) ->
	{:cont, {floor + 1, idx + 1}}
(?), {floor, idx}) -> 
	tag = if (floor - 1) == -1, do: :halt, else: :cont 
	{tag, { floor - 1, idx + 1}}
end)

IO.puts idx
